mapper.select:
select bno, roomNo, stayDate, people, id, to_char(stayDate, 'yyyy') as year, to_char(stayDate, 'mm') as month, to_char(stayDate, 'dd') as day from booking
where id=#{id}
order by bno desc

mapper.update:
update booked set inoutck=#{inoutck}
where
startDate=#{startDate}
and roomNo=#{roomNo }

update booked set inoutck = 3
where
id=#{id}
and
startDate=#{startDate}
and roomNo=#{roomNo }

mapper.select:
select rSize, price from room
where
roomNo=#{roomNo}
